!/bin/bash########################################################################	Author:	lilo#	Date:	2012-12-10#######################################################################TAR='/bin/tar'LOGDIR='/backup/oracle/urpdb/logs'RM="/bin/rm"######################################################################## Go#######################################################################cd $LOGDIR$TAR jcvf backup-log.tar.bz2 . --exclude *.tar.bz2if [ "$0" -ne 0]then    echo "Tar error,please contact the administrator!"    exit 1fi$RM -f *.logif [ "$0" -ne 0]    then    echo "Tar error,please contact the administrator!"    exit 1fi